package operators;

public class BitWiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a = 12;
			int b = 14;
			int c = a& b; // 12(op) bitwise AND operation 
			int d = a|b;// 14 bitwise OR operation
			int e = a>>1; //6 right sifting
			int f = a<<2;// 48 bitwise left shifting 
			System.out.println(c);
			System.out.println(d);
			System.out.println(e);
			System.out.println(f);
			//System.out.println();
	}

}
